import React, { useEffect, useRef, useState } from 'react';
import { searchVideos } from './api';
import SearchBar from './components/SearchBar';
import VideoList from './components/VideoList';
import VideoPlayer from './components/VideoPlayer';

function App() {
  const [videos, setVideos] = useState([]);
  const [selectedVideo, setSelectedVideo] = useState(null);
  const videoPlayerRef = useRef(null); // Reference to the VideoPlayer

  const handleSearch = async (query) => {
    const results = await searchVideos(query);
    setVideos(results);
    setSelectedVideo(results[0]); // Automatically play the first result
  };

  const handleVideoSelect = (video) => {
    setSelectedVideo(video);
  };

  // Scroll to video player when a new video is selected
  useEffect(() => {
    if (selectedVideo && videoPlayerRef.current) {
      videoPlayerRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  }, [selectedVideo]);

  return (
    <div>
      <SearchBar onSearch={handleSearch} />
      <div className="main-content">
        <div ref={videoPlayerRef}>
          <VideoPlayer video={selectedVideo} />
        </div>
        <VideoList videos={videos} onVideoSelect={handleVideoSelect} />
      </div>
    </div>
  );
}

export default App;
